        <div>
          <h1> Herb Identification </h1>{" "}
          <input type="file" accept="image/*" onChange={handleFileUpload} />{" "}
          <button onClick={handleSubmit}> Submit </button>{" "}
          {prediction && <p> Prediction: {prediction} </p>}{" "}
        </div>
      )}{" "}
      {activePage === "service" && <p> Service Page Content </p>}{" "}
    </div>
  );
}

export default App;
